import React, { Component } from 'react';
import {connect} from 'react-redux';
class Button extends Component {
	constructor(props){
		super(props);
		this.state = {
			key : this.props.thongtincaptinh.key,
			queQR : this.props.thongtincaptinh.queQR,
			time : this.props.thongtincaptinh.time,
			time2 : this.props.thongtincaptinh.time2,
		};
	}
	getshowthongtin = () => {
		var info = {};
		let check = "1";
		info.key = this.state.key;
		console.log(this.state.key);
		info.check = check;
		this.props.ThucHienUpdatesss2(info);
	}
	render() { 
		return (
			<button type="button" className="btn btn-warning right"  onClick={()=>this.getshowthongtin()}>
				<i className="fas fa-key"></i>
			</button>
		);
	}
}
const mapStateToProps = (state, ownProps) => {
	return {
			ThucHienUpdate: state.editmaQr
	}
}
const mapDispatchToProps = (dispatch, ownProps) => {
	return {
			ThucHienUpdatesss2: (getupdate) => {
					dispatch({type:'EDIT_DATA_QR_CODE',getupdate})
			},
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(Button)
