import React, { Component } from 'react';
import QrReader from 'react-qr-reader';
class Login extends Component {
	constructor(props) {
		super(props);
		this.state = {
				result: '',
				changDelay : 300,
				cam : 'environment',
				cam2 : 600
		};
}
handleScan = data => {
	if (data) {
		this.setState({
			result: data
		})
	}
}
handleError = err => {
	console.error(err)
}

showKetQua = () => {
	if(this.state.result === '')
	{
		return(
			<div className="alert alert-success" role="alert">
			<h4 className="alert-heading">Bạn hãy đưa QR code đến sát camera</h4>
			<p>Warning</p>
			<hr />
		</div>
		)
	}
	else
	{
		return(
			<div className="alert alert-success" role="alert">
			<h4 className="alert-heading">Kết quả trả về</h4>
			<p>{this.state.result}</p>
			<hr />
		</div>
		)
	}
}

changeState = (event) => {
	const value = event.target.value;
	this.setState({
		changDelay : value
	});



}


changeState2 = (event) => {
	const value = event.target.value;
	this.setState({
		cam : value
	});



}

changeState3 = (event) => {
	const value = event.target.value;
	this.setState({
		cam2 : value
	});



}

camName = () => {
	if(this.state.cam2 === 'user')
	{
		return(
			<p>camera trước</p>
		)
	}
	else
	{
		return(
			<p>camera sau</p>
		)
	}
}
	render() { 
		return (
			<div className="App">
			<header className="App-header">
				<p>Đăng nhập + đăng ký nhanh</p>



				<button className="btn btn-success fa fa-github" onClick={() => this.props.authenticate("Github")}>
				</button>

				<div>
					<p>Hoặc</p>
				</div>

				<button className="btn btn-primary fa fa-facebook" onClick={() => this.props.authenticate("Facebook")}>
				</button>
			</header>
			</div>
		);
	}
}

export default Login;
