import React, { Component , Fragment } from "react";
import Login from "./Login";
import   firebase from "firebase";
import  { firebaseApp } from "../firebaseconnectio";
import DieuHuongUrl from  './../router/DieuHuongUrl';
class UserInfo extends Component {
    state = {
       			email: null,
				displayName: null,
				photoURL : null,
				uid : null
      };

      componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
          if (user) {
            this.authHandler({ user });
		  }
		 this.setState({
			 uid : user.uid
		 });
		});

      }


      authHandler = async authData => {
        console.log(authData);
        const user = authData.user;
        this.setState({
          email: user.email,
					displayName: user.displayName,
					photoURL : user.photoURL
        });
      };

      authenticate = provider => {
        console.log(provider);
        const authProvider = new firebase.auth[`${provider}AuthProvider`]();
        firebaseApp
          .auth()
          .signInWithPopup(authProvider)
          .then(this.authHandler);
      };

      logout = async () => {
        console.log("logout");
        await firebase.auth().signOut();
        this.setState({ email: null, displayName: null , photoURL : null });
	  };


	  phanQuyenNhe = () => {
		  if(this.state.uid === "IJGMZnvrF8QB2BN7GFNDID91eX93")
		  {
			  return(
				<div>
					<a className="nav-link" href="/thong-ke">	<i className="fas fa-fw fa-tachometer-alt" /><span  style={{marginLeft: '15px'}}>Dashboard</span></a>
					<a className="nav-link" href="/hien-thi-danh-sach">	<i className="fas fa-address-book" /><span  style={{marginLeft: '15px'}}>Danh sách tài liệu</span></a>
					<a className="nav-link" href="/du-lieu-giong-noi">	<i className="fas fa-microphone" /><span  style={{marginLeft: '15px'}}>Dữ liệu giọng nói</span></a>
					<a className="nav-link" href="/du-lieu-update-no-ai">	<i className="far fa-grin-tongue-wink" /><span  style={{marginLeft: '15px'}}>Mặt trước no AI </span></a>
					<a className="nav-link" href="/thong-tin-he-thong">	<i className="fas fa-tape" /><span  style={{marginLeft: '15px'}}>Update API</span></a>
					<a className="nav-link" href="/danh-sach">	<i className="fas fa-list-alt" /><span  style={{marginLeft: '15px'}}>Danh sách</span></a>
					<a className="nav-link" href="/mat-truoc">	<i className="fas fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt trước</span></a>
					<a className="nav-link" href="/mat-sau">	<i className="far fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt sau</span></a>
					<a className="nav-link" href="/giong-noi">	<i className="fas fa-microphone-alt" /><span  style={{marginLeft: '15px'}}>Giọng nói</span></a>
				</div>
			  )
		  }
		  else
		  {
			  return(
				<div>
										<a className="nav-link" href="/du-lieu-update-no-ai">	<i className="far fa-grin-tongue-wink" /><span  style={{marginLeft: '15px'}}>Mặt trước no AI </span></a>
					<a className="nav-link" href="/mat-truoc">	<i className="fas fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt trước</span></a>
					<a className="nav-link" href="/mat-sau">	<i className="far fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt sau</span></a>
				</div>
			  )
		  }
	  }



  render() {
   const logout = <button onClick={this.logout}>Log Out!</button>;
    if (!this.state.email) {
      return <Login authenticate={this.authenticate} />;
		}
		console.log(this.state);
    return (
     <Fragment>
      	<div>


							<nav className="navbar navbar-expand navbar-dark bg-dark static-top">
								<a className="navbar-brand mr-1" href="index.html"><img src="https://z9t4u9f6.stackpathcdn.com/wp-content/uploads/2018/06/logo-mdb-jquery-small.png" alt="iconImage" /></a>
								<button className="btn btn-link btn-sm text-white order-1 order-sm-0" id="sidebarToggle" href="#">
									<i className="fas fa-bars" />
								</button>
								{/* Navbar Search */}
								<form className="d-none d-md-inline-block form-inline ml-auto mr-0 mr-md-3 my-2 my-md-0">
								</form>
								{/* Navbar */}
								<ul className="navbar-nav ml-auto ml-md-0">
									<li className="nav-item dropdown no-arrow">
										<a className="nav-link dropdown-toggle" href="/" id="userDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
										<img src={this.state.photoURL} alt="logo" style={{width: '40px'}} />
										</a>
										<div className="dropdown-menu dropdown-menu-right" aria-labelledby="userDropdown">
											<a className="dropdown-item" href="/"> <span>{this.state.displayName}</span></a>
											<a className="dropdown-item" href="/"> <span>{this.state.email}</span></a>
											<div className="dropdown-divider" />
											<a className="dropdown-item" href="/" data-toggle="modal" data-target="#logoutModal">{logout}</a>
										</div>
									</li>
								</ul>
							</nav>






					</div>
      </Fragment>
    );
  }
}

export default UserInfo;
