import React, { Component } from 'react';
import QrReader from 'react-qr-reader';
class Login extends Component {
	constructor(props) {
		super(props);
		this.state = {
				result: '',
				changDelay : 300,
				cam : 'environment',
				cam2 : 600
		};
}
handleScan = data => {
	if (data) {
		this.setState({
			result: data
		})
	}
}
handleError = err => {
	console.error(err)
}

showKetQua = () => {
	if(this.state.result === '')
	{
		return(
			<div className="alert alert-success" role="alert">
			<h4 className="alert-heading">Bạn hãy đưa QR code đến sát camera</h4>
			<p>Warning</p>
			<hr />
		</div>
		)
	}
	else
	{
		return(
			<div className="alert alert-success" role="alert">
			<h4 className="alert-heading">Kết quả trả về</h4>
			<p>{this.state.result}</p>
			<hr />
		</div>
		)
	}
}

changeState = (event) => {
	const value = event.target.value;
	this.setState({
		changDelay : value
	});



}


changeState2 = (event) => {
	const value = event.target.value;
	this.setState({
		cam : value
	});



}

changeState3 = (event) => {
	const value = event.target.value;
	this.setState({
		cam2 : value
	});



