import React from "react";
import QrReader from 'react-qr-reader';
import '../../src/App.css';

function Login(props) {

handleScan = data => {
	if (data) {
		this.setState({
			result: data
		})
	}
}
handleError = err => {
	console.error(err)
}

showKetQua = () => {
	if(this.state.result === '')
	{
		return(
			<div className="alert alert-success" role="alert">
			<h4 className="alert-heading">B·∫°n h√£y ƒë∆∞a QR code ƒë·∫øn s√°t camera</h4>
			<p>Warning</p>
			<hr />
		</div>
		)
	}
	else
	{
		return(
			<div className="alert alert-success" role="alert">
			<h4 className="alert-heading">K·∫øt qu·∫£ tr·∫£ v·ªÅ</h4>
			<p>{this.state.result}</p>
			<hr />
		</div>
		)
	}
}

changeState = (event) => {
	const value = event.target.value;
	this.setState({
		changDelay : value
	});



}


changeState2 = (event) => {
	const value = event.target.value;
	this.setState({
		cam : value
	});



}

changeState3 = (event) => {
	const value = event.target.value;
	this.setState({
		cam2 : value
	});



}

camName = () => {
	if(this.state.cam2 === 'user')
	{
		return(
			<p>camera tr∆∞·ªõc</p>
		)
	}
	else
	{
		return(
			<p>camera sau</p>
		)
	}
}


  return (
		<div className="App">
		<header className="App-header">


		<section>

											<div className="container">
          <div className="row mbr-justify-content-center">
            <div className="col-lg-6 mbr-col-md-6">
            <QrReader
                    showViewFinder={true}
                    facingMode={this.state.cam}
                    delay={this.state.changDelay}
                    onError={this.handleError}
                    onScan={this.handleScan}
                    style={{ width: '100%' }}
                    />
                  <div>
                    {this.showKetQua()}
                  </div>
            </div>
            <div className="col-lg-6 mbr-col-md-6">
            <div className="alert alert-success" role="alert">
              <h4 className="alert-heading">Tinh ch·ªânh c√†i ƒë·∫∑t m·ªôt t√≠</h4>
