import React, { Component , Fragment } from "react";
import Login from "./Login";
import   firebase from "firebase";
import {firebasefor} from '../firebaseconnectio';
import dl from '../components/Data/Maqrcode.json';
import  { firebaseApp } from "../firebaseconnectio";
import DieuHuongUrl from  './../router/DieuHuongUrl';
class UserInfo extends Component {
    state = {
       	email: null,
				displayName: null,
				photoURL : null,
				uid : null,
				dataBaseQR : [],
				dulieuQRcode : [],
			};


			componentWillMount() {
				if(localStorage.getItem('komsa') === null)
				{
					localStorage.setItem('komsa',JSON.stringify(dl));
				}
				else
				{
					var temp = JSON.parse(localStorage.getItem('komsa'));
					this.setState({
						dulieuQRcode : temp,
					});
				}
				firebasefor.on('value',(datas) => {
					var Mang = [];
					datas.forEach(element => {
						const key = element.key;
						const queQR = element.val().queQR;
						const time = element.val().time;
						const time2 = element.val().time2;
						const check = element.val().check;
						if(check === "1"){
							Mang.push({
								key : key,
								queQR : queQR,
								time : time,
								time2 : time2
							})
						}
					});
					this.setState({
						dataBaseQR : Mang
					});
				})
			}





      componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
          if (user){
            this.authHandler({ user });
					}
				this.setState({
					uid : user.uid
				});
				});

      }


      authHandler = async authData => {
        console.log(authData);
        const user = authData.user;
        this.setState({
          email: user.email,
					displayName: user.displayName,
					photoURL : user.photoURL
        });
      };

      authenticate = provider => {
        console.log(provider);
        const authProvider = new firebase.auth[`${provider}AuthProvider`]();
        firebaseApp
          .auth()
          .signInWithPopup(authProvider)
          .then(this.authHandler);
      };

      logout = async () => {
        console.log("logout");
        await firebase.auth().signOut();
        this.setState({ email: null, displayName: null , photoURL : null });
		};

	  phanQuyenNhe = () => {

			this.state.dataBaseQR.forEach((item) => {
				if(item.key.indexOf(this.state.dulieuQRcode) !== -1)
				{
				return(
							<div>
								<a className="nav-link" href="/thong-ke">	<i className="fas fa-fw fa-tachometer-alt" /><span  style={{marginLeft: '15px'}}>Thống kê</span></a>
								<a className="nav-link" href="/mat-truoc">	<i className="fas fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt trước</span></a>
								<a className="nav-link" href="/du-lieu-update-no-ai">	<i className="far fa-grin-tongue-wink" /><span  style={{marginLeft: '15px'}}>Mặt trước no AI </span></a>
								<a className="nav-link" href="/mat-sau">	<i className="far fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt sau</span></a>
								<a className="nav-link" href="/giong-noi">	<i className="fas fa-microphone-alt" /><span  style={{marginLeft: '15px'}}>Tạo văn bản</span></a>
								<a className="nav-link" href="/hien-thi-danh-sach">	<i className="fas fa-address-book" /><span  style={{marginLeft: '15px'}}>Danh sách tài liệu</span></a>
								<a className="nav-link" href="/du-lieu-giong-noi">	<i className="fas fa-archive" /><span  style={{marginLeft: '15px'}}>Dữ liệu giọng nói</span></a>
								<a className="nav-link" href="/danh-sach">	<i className="fas fa-list-alt" /><span  style={{marginLeft: '15px'}}>Danh sách thu thập</span></a>
								<a className="nav-link" href="/thong-tin-he-thong">	<i className="fas fa-cogs" /><span  style={{marginLeft: '15px'}}>Cài đặt</span></a>
							</div>
			  		)
				}
				else
				{
					return(
						<div>
						<a className="nav-link" href="/du-lieu-update-no-ai">	<i className="far fa-grin-tongue-wink" /><span  style={{marginLeft: '15px'}}>Mặt trước no AI </span></a>
						<a className="nav-link" href="/mat-truoc">	<i className="fas fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt trước</span></a>
						<a className="nav-link" href="/mat-sau">	<i className="far fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt sau</span></a>
					</div>
					)
				}
			});

		  // if(this.state.uid === "IJGMZnvrF8QB2BN7GFNDID91eX93")
		  // {
			//   return(
			// 	<div>
			// 		<a className="nav-link" href="/thong-ke">	<i className="fas fa-fw fa-tachometer-alt" /><span  style={{marginLeft: '15px'}}>Thống kê</span></a>
			// 		<a className="nav-link" href="/mat-truoc">	<i className="fas fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt trước</span></a>
			// 		<a className="nav-link" href="/du-lieu-update-no-ai">	<i className="far fa-grin-tongue-wink" /><span  style={{marginLeft: '15px'}}>Mặt trước no AI </span></a>
			// 		<a className="nav-link" href="/mat-sau">	<i className="far fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt sau</span></a>
			// 		<a className="nav-link" href="/giong-noi">	<i className="fas fa-microphone-alt" /><span  style={{marginLeft: '15px'}}>Tạo văn bản</span></a>
			// 		<a className="nav-link" href="/hien-thi-danh-sach">	<i className="fas fa-address-book" /><span  style={{marginLeft: '15px'}}>Danh sách tài liệu</span></a>
			// 		<a className="nav-link" href="/du-lieu-giong-noi">	<i className="fas fa-archive" /><span  style={{marginLeft: '15px'}}>Dữ liệu giọng nói</span></a>
			// 		<a className="nav-link" href="/danh-sach">	<i className="fas fa-list-alt" /><span  style={{marginLeft: '15px'}}>Danh sách thu thập</span></a>
			// 		<a className="nav-link" href="/thong-tin-he-thong">	<i className="fas fa-cogs" /><span  style={{marginLeft: '15px'}}>Cài đặt</span></a>
			// 	</div>
			//   )
		  // }
		  // else
		  // {
			//   return(
			// 	<div>
			// 		<a className="nav-link" href="/du-lieu-update-no-ai">	<i className="far fa-grin-tongue-wink" /><span  style={{marginLeft: '15px'}}>Mặt trước no AI </span></a>
			// 		<a className="nav-link" href="/mat-truoc">	<i className="fas fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt trước</span></a>
			// 		<a className="nav-link" href="/mat-sau">	<i className="far fa-meh" /><span  style={{marginLeft: '15px'}}>Mặt sau</span></a>
			// 	</div>
			//   )
		  // }
	  }



  render() {
   const logout = <button onClick={this.logout}>Log Out!</button>;
    if (!this.state.email) {
      return <Login authenticate={this.authenticate} />;
		}
    return (
     <Fragment>
      	<div>


							<nav className="navbar navbar-expand navbar-dark bg-dark static-top">
								<a className="navbar-brand mr-1 fontssss" href="index.html"><img src="https://www.upsieutoc.com/images/2020/02/12/pngfuel.com.png" className="anhdaidien" alt="iconImage" />One world one dream</a>
								{/* Navbar Search */}
								<form className="d-none d-md-inline-block form-inline ml-auto mr-0 mr-md-3 my-2 my-md-0">
								</form>
								{/* Navbar */}
								<ul className="navbar-nav ml-auto ml-md-0">
									<li className="nav-item dropdown no-arrow">
										<a className="nav-link dropdown-toggle" href="/" id="userDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
										<img src={this.state.photoURL} alt="logo" style={{width: '40px'}} />
										</a>
										<div className="dropdown-menu dropdown-menu-right" aria-labelledby="userDropdown">
											<a className="dropdown-item" href="/"> <span>{this.state.displayName}</span></a>
											<a className="dropdown-item" href="/"> <span>{this.state.email}</span></a>
											<div className="dropdown-divider" />
											<a className="dropdown-item" href="/" data-toggle="modal" data-target="#logoutModal">Đăng xuất</a>
										</div>
									</li>
								</ul>
							</nav>





							<div id="wrapper">
								{/* Sidebar */}
								<ul className="sidebar navbar-nav">
									<li className="nav-item active">
										{this.phanQuyenNhe()}
									</li>
								</ul>
								<div id="content-wrapper">
									<div className="container-fluid">
										{/* Breadcrumbs*/}










										<DieuHuongUrl/>




										{/* Sticky Footer */}
										<footer className="sticky-footer">
											<div className="container my-auto">
												<div className="copyright text-center my-auto">
													<span>Copyright © Your Website 2019</span>
												</div>
											</div>
										</footer>
									</div>
									{/* /.content-wrapper */}
								</div>
								{/* /#wrapper */}
								{/* Scroll to Top Button*/}
								<a className="scroll-to-top rounded" href="#page-top">
									<i className="fas fa-angle-up" />
								</a>
								{/* Logout Modal*/}
								<div className="modal fade" id="logoutModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
									<div className="modal-dialog" role="document">
										<div className="modal-content">
											<div className="modal-header">
												<h5 className="modal-title" id="exampleModalLabel">Bạn có muốn đăng xuất không?</h5>
												<button className="close" type="button" data-dismiss="modal" aria-label="Close">
													<span aria-hidden="true">×</span>
												</button>
											</div>
											<div className="modal-body">Nếu đồng lý hãy chọn Logout nếu không đồng ý hãy chọn Cancel</div>
											<div className="modal-footer">
												<button className="btn btn-secondary" type="button" data-dismiss="modal">Cancel</button>
													<button className="btn btn-primary" type="button">{logout}</button>
											</div>
										</div>
									</div>
								</div>
							</div>	
					</div>
      </Fragment>
    );
  }
}

export default UserInfo;
