import React, { Component } from 'react';
import {connect} from 'react-redux';
import {firebasethree} from '../../firebaseconnectio';
import {firebasefor} from '../../firebaseconnectio';
class Infos extends Component {
	constructor(props) {
		super(props);
		this.state = {
			api : '',
			datas : [],
			dataQr : []
		};
	}
	componentWillMount() {
		firebasethree.on('value',(datas) => {
			var Mang = [];
			datas.forEach(element => {
				const data = element.val();
				Mang.push(data);
			});
			this.setState({
				datas : Mang
			});
		})
		firebasefor.on('value',(dataQr) => {
      var Mang = [];
      dataQr.forEach(element => {
        const key = element.key;
        const queQR = element.val().queQR;
        const time = element.val().time;
				const time2 = element.val().time2;
				const check = element.val().check;
				if(check !== "1"){
					Mang.push({
						key : key,
						queQR : queQR,
						time : time,
						time2 : time2
					})
				}
      });
      this.setState({
        dataQr : Mang,
      })
    })
	}

	phantichthanhcong = () => {
			 this.state.datas.map((item) => {
			 	if(item === this.state.datas[this.state.datas.length - 1])
			 	{
					return(<small>Mã này đã từng tồn tại</small>)
			 	}
			 	else
			 	{
					return(
						<h1>Thêm mới thành công</h1>
					);
			 	}
			 })
	}

	IsChange = (event) => {
		const name = event.target.name;
		const value = event.target.value;
		this.setState({
			[name] : value
		});
		console.log(this.state);
	}

	Buttonupdate = () => {
		this.props.Thaydoidulieuvonco(this.state.api);
		console.log(this.props.Showdulieu);
	}

 render() {
	 console.log(this.props.Showdulieu.Nhangiatriapimoi);
	 console.log(this.state.dataQr);
	 return (
		 <div>
			<div className="form-group">
				<label/>
				{this.phantichthanhcong()}
				<div className="input-group-append">
				<input type="text" className="form-control" onChange={(event)=>this.IsChange(event)}   placeholder={this.props.Showdulieu.Nhangiatriapimoi} name="image_url" aria-label="Recipient's username" aria-describedby="basic-addon2" />

                    <span className="input-group-text" id="basic-addon2">Đây là ô nhập liệu APi mới</span>
                  	<button type="button" onClick={()=>this.Buttonupdate()} name="" id="" className="btn btn-primary"><i className="fab fa-adobe"></i></button>
                    </div>

				<table className="table" id="table-to-xls" border="1">
              <thead className="thead-dark" style={{fontSize: '12px'}}>
                <tr >
                  <th scope="col">STT</th>
                  <th scope="col">Mã code</th>
                  <th scope="col">Số phiên quét mã</th>
                  <th scope="col">Mã đã giải</th>
                  <th scope="col">Ngày quét</th>
									<th scope="col">Thời gian quyét</th>
									<th scope="col">Cho phép vào</th>
                </tr>
              </thead>
							<tbody style={{fontSize: '10px'}}>
          {
              this.state.dataQr.map((value , key) => {
                  return (
                      <tr key={key}>
                           			<th scope="row">{key}</th>
																 <th scope="row">{value.key}</th>
                                <td>{key}</td>
                                <td>{value.queQR}</td>
																<td>{value.time}</td>
																<td>{value.time2}</td>
																<td>
																<button type="button" className="btn btn-secondary">Secondary</button>
																</td>
                      </tr>
                  )
              })
          }
    </tbody>


            </table>
			</div>
		 </div>
	 )
 }
}
const mapStateToProps = (state, ownProps) => {
	return {
		Showdulieu: state.nhaplieuapi
	}
}
const mapDispatchToProps = (dispatch, ownProps) => {
	return {
		Thaydoidulieuvonco: (getdata) => {
			dispatch({type:'ADD_API',getdata})
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(Infos);
