import React, { Component } from 'react';
import {connect} from 'react-redux';
import {firebasethree} from '../../firebaseconnectio';
class Infos extends Component {
	constructor(props) {
		super(props);
		this.state = {
			api : '',
			datas : []
		};
	}
	componentWillMount() {
		firebasethree.on('value',(datas) => {
			var Mang = [];
			datas.forEach(element => {
				const data = element.val();
				Mang.push(data);
			});
			this.setState({
				datas : Mang
			});
		})
	}

	phantichthanhcong = () => {
			 this.state.datas.map((item) => {
			 	if(item === this.state.datas[this.state.datas.length - 1])
			 	{
					alert("api từng tồn tại")
			 	}
			 	else
			 	{
					return(
						<h1>Thêm mới thành công</h1>
					);
			 	}
			 })
	}

	IsChange = (event) => {
		const name = event.target.name;
		const value = event.target.value;
		this.setState({
			[name] : value
		});
		console.log(this.state);
	}

	Buttonupdate = () => {
		this.props.Thaydoidulieuvonco(this.state.api);
		console.log(this.props.Showdulieu);
	}

 render() {
	 console.log(this.props.Showdulieu.Nhangiatriapimoi);
	 return (
		 <div>
			<div className="form-group">
				<label/>
				<input type="text" onChange={(event)=>this.IsChange(event)} className="form-control" name="api" aria-describedby="helpId" placeholder={this.props.Showdulieu.Nhangiatriapimoi} />
				<button type="button" onClick={()=>this.Buttonupdate()} name="" id="" className="btn btn-primary">Update API</button>
				<input type="text" className="form-control" onChange={(event)=>this.IsChange(event)}   placeholder={this.props.Showdulieu.Nhangiatriapimoi} name="image_url" aria-label="Recipient's username" aria-describedby="basic-addon2" />
                    <div className="input-group-append">
                    <span className="input-group-text" id="basic-addon2">.JPG/.JPEG/.PNG</span>
                    <button type="button" className="btn btn-success" onClick={()=>this.Haller()}><i className="fas fa-archway"></i></button>
                    <button type="button" className="btn btn-info" onClick={()=>this.ButtonsClean()}><i className="fas fa-cloud-upload-alt"></i></button>
                    <button type="button" className="btn btn-warning" onClick={()=>this.ButtonsClean2()}><i className="fas fa-eraser"></i></button>
										<button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter"><i className="fas fa-cogs"></i></button>
										<button type="button" className="btn btn-dark" data-toggle="modal" data-target="#exampleModalCenter2"><i className="fas fa-video"></i></button>
                    </div>
				{this.phantichthanhcong()}
			</div>
		 </div>
	 )
 }
}
const mapStateToProps = (state, ownProps) => {
	return {
		Showdulieu: state.nhaplieuapi
	}
}
const mapDispatchToProps = (dispatch, ownProps) => {
	return {
		Thaydoidulieuvonco: (getdata) => {
			dispatch({type:'ADD_API',getdata})
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(Infos);
