import {firebasefor} from '../firebaseconnectio';

const initState = {
    getdata: {},
};

const reducer10 = (state = initState, action) => {
    switch (action.type) {

        case "EDIT_DATA_QR_CODE_KH":
					console.log(action.getupdate.key);
					console.log(action.getupdate.check);
					firebasefor.child(action.getupdate.key).update({
            check : action.getupdate.check,
        })
        return{...state,getdata:action.getitem}
        default:
            return state;
    }
};

export default reducer10;
